void load_to_spram(unsigned char* data,unsigned int data_len, unsigned int data_offset, unsigned int spram_address);

void sleep_msec(unsigned msec) {
	unsigned i=0;
	while (msec>0) {
		while (i<10000) {
			__asm__("nop");
			++i;
		}
		--msec;
	}
}

void speak_demo() {
	char i=0;
	unsigned int sp_size=0;

	clrscr();
	
	sp_size = sizeof(hi)/sizeof(hi[0]);
	
	printf("Text size: %d\n",sp_size);
	
	printf("SPcard Reset - ");
	spcard_reset();
	printf("Done\n");
	
	printf("SPRAM COPY hi - ");
	load_to_spram((unsigned char*)hi,sp_size,0,0x100);
	printf("Done\n");

	printf("Talk hi - ");
	spcard_say(0x100,1);
	printf("Done\n");
	sleep_msec(2000);
	
	printf("RAM hi2 - ");
	printf("Size: %d - ",sizeof(hi2));
	spcard_ram_say((unsigned char*)hi2,0,sizeof(hi2),0,1);
	printf("Done\n");
	sleep_msec(2000);
	printf("RAM welcome - ");
	printf("Size: %d - ",sizeof(welcome));
	spcard_ram_say((unsigned char*)welcome,0,sizeof(welcome),0,1);
	printf("Done\n");
	sleep_msec(2000);
	printf("RAM russia-p - ");
	printf("Size: %d - ",sizeof(rossia8000cp));
	spcard_ram_say((unsigned char*)rossia8000cp,0,sizeof(rossia8000cp),0,1);
	printf("Done\n");

	cgetc();
}

char main_menu () {
	char inchar;
	int i;
	clrscr();
	gotoxy (10,3);
	printf("AGAT Speech Board\n");
	gotoxy (15,6);
	printf("Speech demo\n");

	gotoxy (8,9);
	cputs("[");revers(1);cputs("1");revers(0);cputs("]");
	cputs(" Start");
	gotoxy (8,15);
	cputs("[");revers(1);cputs("0");revers(0);cputs("]");
	cputs(" Exit");

	
	while ((inchar=cgetc())!='0') {
		if (inchar=='1') speak_demo();
		return 0;
	}
	return 1;
}


int main(void) {
	
	while (!main_menu());
	
	printf("\n\nThats all for now...\n");
	return 0;
}